import "./App.css";
import React, { useState } from "react";
import Tasks from "./Tasks";

class App extends Tasks {
  state = { tasks: [], currentTask: "" };

  render() {
    const { todos } = this.state;
    return (
      <div className="todos">
        <h1>todos</h1>
        <div className="todo-list">
          <div className="input-container">
            {/* <button className="markAll-button" onClick={}></button> */}
            <input
              className="input"
              onKeyDown={this.handleSubmit}
              placeholder="What needs to be done?"
              value={value}
              onChange={this}
              />
          </div>
          <div>
      <ul>
        {todos.map((item) => (
          <li className="list-item" key={item.id}>
            <div className="liContainer">
              <input
                className="checkbox"
                id={item.id}
                checked={item.completed}
                type="checkbox"
                onChange={() => this.handleUpdate(item.id)}
              ></input>
              <label
                className={item.completed ? "close" : ""}
                htmlFor={item.id}
              >
                {item.title}
              </label>
            </div>
            <button
              className="delete-button"
              onClick={() => this.handleDelete(item.id)}
            />
          </li>
        ))}
      </ul>
    </div>
    <div className="footer-container">
      <div className="result">
        {todos.filter((item) => !item.completed).length} items left{" "}
      </div>

      <div className="btnGroup">
        <button
          className={filter === "all" ? "used-filter-btn" : "unused-filter-btn"}
          onClick={() => setFilter("all")}
        >
          All
        </button>
        <button
          className={
            filter === "active" ? "used-filter-btn" : "unused-filter-btn"
          }
          onClick={() => setFilter("active")}
        >
          Active
        </button>
        <button
          className={
            filter === "completed" ? "used-filter-btn" : "unused-filter-btn"
          }
          onClick={() => setFilter("completed")}
        >
          Completed
        </button>
      </div>
      <button className="delete-completed-btn" onClick={deleteTodos}>
        Delete completed
      </button>
    </div>
        </div>
      </div>
    );
  }
}
export default App;
